import React, {useReducer, createContext} from 'react'
import Card from './Components/Card';
import Button from './Components/Btn'
import CardDetails from './Components/Users';

export const UserContext = createContext();
const Details = {
  cardDetails : CardDetails,
  CurrentCard : {}
}

const Reducer = (state, action) => {
  // console.log("Reducer function is called " + typeof action.payload.id)
  switch(action.type){
    case "UpdateCard":
      //console.log(state?.cardDetails[action.payload.id])
      console.log(state)
      return {...state, CurrentCard:state.cardDetails[action.payload.id]}

      // state
      // return state.cardDetails.map((data,index) => {
      //   if (index === action.payload.id){
      //      state.CurrentCard = data
      //   }
      //   // console.log("jhvbvcvv")
      //   return data
      // })
      break;
    default:
      return state
  }
  console.log(state)
}

function App() {
 const [state, dispatch] = useReducer(Reducer, Details)

  return (
    <UserContext.Provider value={Details}>
    <div className='Home-Page'>
        <Card />
        <Button dispatch={dispatch}/>
    </div>
    </UserContext.Provider>
  );
}

export default App;
