import React, {useContext,useReducer, createContext} from 'react'
import Card from './Components/Card';
import Button from './Components/Btn'
import CardDetails from './Components/Users';

export const UserContext = createContext();
const Details = {
  cardDetails : CardDetails,
  CurrentCard : {
    Bank_Name : "Hackker Bank",
    Card_No : "1234 5678 9012 3456",
    Expire : "02/23",
    Cvv : "123",
    Name : 'Goutham Ramesh'
  }
}

const Reducer = (state, action) => {
  console.log("Reducer function is called " + action.payload.id)
  switch(action.type){
    case "UpdateCard":
      return state.cardDetails.map((data, index) => console.log(data,index))
      // return state.cardDetails.map((data, index) => index === action.payload.id ? state.CurrentCard = data : {})
      // state.CurrentCard = action.payload
      // break;
    default:
      return state
  }
}

function App() {
 const [state, dispatch] = useReducer(Reducer, Details)

  return (
    <UserContext.Provider value={state}>
    <div className='Home-Page'>
        <Card />
        <Button dispatch={dispatch}/>
    </div>
    </UserContext.Provider>
  );
}

export default App;
