import React, {useReducer, createContext} from 'react'
import Card from './Components/Card';
import Button from './Components/Btn'
import CardDetails from './Components/Users';

export const UserContext = createContext();
const Details = {
  cardDetails : CardDetails,
  CurrentCard : {}
}

const Reducer = (state, action) => {
  console.log("Reducer function is called " + typeof action.payload.id)
  switch(action.type){
    case "UpdateCard":
      state.cardDetails.forEach(function(data,index){
        if (index === action.payload.id){
          state.CurrentCard = data
        }
      })
      console.log(state.CurrentCard)
      break;
    default:
      return state
  }
}

function App() {
 const [state, dispatch] = useReducer(Reducer, Details)

  return (
    <UserContext.Provider value={state}>
    <div className='Home-Page'>
        <Card />
        <Button dispatch={dispatch}/>
    </div>
    </UserContext.Provider>
  );
}

export default App;
